import argparse
import re
import random
import shutil

DOGEHOUSE = (
    "standard",
    "rose",
    "bunny",
    "xmas",
    "hat",
    "pumpkin",
    "earth",
)


def getargs() -> tuple[str, str, int]:
    """Get command line arguments
    Returns:
      Tuple containing (doge_art, text, offset)
    """

    def unescape_string(s: str) -> str:
        """Internal function: Unescape string
        Args:
          s (str): String with escape sequences
        Returns:
          str: Unescaped string
        """

        escape_sequences = {
            "\\n": "\n",
            "\\t": "\t",
            "\\r": "\r",
            "\\\\": "\\",
            '\\"': '"',
            "\\'": "'",
        }
        result = ""
        i = 0
        while i < len(s):
            if s[i] == "\\" and i + 1 < len(s):
                escape = s[i : i + 2]
                if escape in escape_sequences:
                    result += escape_sequences[escape]
                    i += 2
                    continue
            result += s[i]
            i += 1
        return result

    parser = argparse.ArgumentParser()
    parser.add_argument(
        "--doge",
        "-d",
        type=str,
        default=random.choice(DOGEHOUSE),
        choices=list(DOGEHOUSE),
        help=f"choose your doge",
    )
    parser.add_argument(
        "--text",
        "-t",
        type=str,
        default="",
        help='text to add to doge, example: -t "Wow!\\n\\tVery cool!"',
    )
    parser.add_argument(
        "--offset",
        "-o",
        type=int,
        default=2,
        help="integer >= 0, number of tabs between text and doge",
    )
    args = parser.parse_args()

    if args.doge not in DOGEHOUSE:
        parser.error(f"--doge must be one of {DOGEHOUSE}")

    if args.offset < 0:
        parser.error("--offset must be a non-negative integer")

    return eval(args.doge), unescape_string(args.text), args.offset


def add_text_to_doge(doge: str, text: str = "", offset: int = 2) -> str:
    """Add text to doge
    Args:
      doge (str): The doge ASCII art
      text (str, optional): Text to add. Defaults to "".
      offset (int, optional): Number of tabs between text and doge. Default: 2.
    Returns:
      str: Combined doge and text. Returns original doge if terminal width is too small.
    """

    if not text:
        return doge

    # Split doge and text into lines
    doge_lines = doge.split("\n")
    text_lines = text.split("\n")

    # Start line for text
    start = len(doge_lines) // 2 - len(text_lines) // 2

    # If terminal width is too small, return doge without text

    max_doge_width = 0
    for line in doge_lines:
        clean_line = re.sub(r"\033\[[0-9;]*[mK]", "", line)
        clean_line = clean_line.rstrip()
        max_doge_width = max(max_doge_width, len(clean_line))

    max_text_width = 0
    for line in text_lines:
        expanded_line = line.expandtabs(8)
        max_text_width = max(max_text_width, len(expanded_line))

    if (
        max_doge_width + (offset * 8) + max_text_width
        > shutil.get_terminal_size().columns
    ):
        return doge

    # Add text to doge
    for i, txt in enumerate(text_lines):
        if 0 <= start + i < len(doge_lines):
            doge_lines[start + i] = (
                f"{doge_lines[start + i].rstrip()}{'\t' * offset}{txt}"
            )

    # Return combined doge and text
    return "\n".join(doge_lines)


if __name__ == "__main__":

    # Get arguments and print the doge with text
    doge, text, offset = getargs()
    print(add_text_to_doge(doge, text, offset))
